display.description:
${groovy: 
   			if(maritalStatus=='S') 
   				return 'Single'; 
   			else if(maritalStatus=='M') 
   				return 'Married'; 
   			else if(maritalStatus=='P') 
   				return 'Separated'; 
			else if(maritalStatus=='D') 
   				return 'Divorced'; 
  			else if(maritalStatus=='W') 
  				return 'Widowed';
   			else return 'Single';}

${groovy: 
   			if(jobRequisition.gender=='M') 
   				return 'Male'; 
   			else if(jobRequisition.gender=='F') 
   				return 'Female'; 
   			else return 'Other';}
